@import url("https://fonts.googleapis.com/css2?family=Jost:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;1,100&display=swap");

* {
    box-sizing: border-box;
    font-family: "Jost", sans-serif;
}

a {
    text-decoration: none;
}

li {
    list-style: none;
}

/* 

/* navbar style start  */

.main-nav {
    /* width: 100%; */
    height: 5.4rem;
    display: grid;
    grid-template-columns: 10rem 1fr 2fr 1fr 10rem;
    box-shadow: rgba(50, 50, 93, 0.25) 0px 5px 10px -2px,
        rgba(0, 0, 0, 0.3) 0px 3px 6px -3px;
}

.logo {
    display: grid;
    /* background-color: #3b5998; */
    grid-column: 2/3;
    justify-content: start;
    padding-top: 4px;
    letter-spacing: .3rem;
}

.logo2 {
    height: 4.8rem;
    width: 9rem;
    padding: 10px 0 0 65px;
}

.menu-link {
    grid-column: 3/4;
    padding-left: 26rem;
    width: 75rem;
}

.menu-link ul {
    height: 10rem;
    display: flex;
    justify-content: space-around;
}

.menu-link ul li {
    padding: 1.2rem 0 0 0;
    color: rgb(22, 12, 65);
    font-size: 1.5rem;
    font-weight: 500;
}

.social-media {
    grid-column: 4/5;
}

.social-media ul {
    height: 10rem;
    display: grid;
    grid-template-columns: 3fr repeat(3, 1fr);
    justify-content: flex-end;
}

.social-media ul li {
    text-align: right;
}

.social-media ul li:first-child {
    grid-column: 2/3;
}

/* ----------- Grid part ends ----------------- */

.logo h2 {
    font-size: 2.5rem;
    font-weight: 400;
    text-transform: uppercase;
    /* background: -webkit-linear-gradient(#eeeeeeb0, rgb(42, 30, 209));
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent; */
    color: rgb(22, 12, 65);
}

.logo h2 span {
    font-size: 3.5rem;
}

.menu-link ul li {
    font-size: 1.8rem;
}

.menu-link ul li a {
    text-transform: capitalize;
    color: rgba(0, 0, 3, 0.8);
    transition: 0.5s;
}

.menu-link ul li:hover>a {
    transform-origin: left;
    color: rgba(0, 0, 3, 1);
    text-decoration: underline;
    transition: 0.5s;
}

.social-media ul li {
    font-size: 1.8rem;
}

.social-media .hamburger-menu {
    display: none;
}

.facebook {
    color: #3b5998;
}

.instagram {
    color: #c32aa3;
}

.youtube {
    color: #ff0000;
}

/* hero section   */

.hero-section {
    height: 80vh;
    display: flex;
    justify-content: center;
    flex-direction: column;
}

.hero-section p {
    font-size: 3rem;
    text-transform: capitalize;
}

.hero-section h1 {
    font-size: 5rem;
    text-transform: uppercase;
    text-align: center;
}

/* responsive css style  */
@media (max-width: 1080px) {
    .main-nav {
        height: 8rem;
        grid-template-columns: 2rem 3fr 3fr 1fr 2rem;
    }

    .logo,
    .menu-link ul,
    .social-media ul {
        height: 8rem;
    }
}

/* responsive css style  */
@media (max-width: 998px) {
    .main-nav {
        height: 7rem;
        grid-template-columns: 2rem 2fr 3fr 2rem 2rem;
    }

    .menu-link {
        display: none;
    }

    .logo,
    .social-media ul {
        height: 7rem;
    }

    .mobile-menu-link {
        grid-column: 2/4;
        position: relative;
        z-index: 99;
    }

    .mobile-menu-link {
        background-color: white;
        height: 20rem;
        display: grid;
        grid-column: 2/5;
        align-items: center;
        padding-left: 3rem;
        transition: all 2s linear;
        transform-origin: top;
        box-shadow: rgba(50, 50, 93, 0.25) 0px 50px 100px -20px,
            rgba(0, 0, 0, 0.3) 0px 30px 60px -30px;
    }

    .mobile-menu-link ul {
        height: 20rem;
        display: flex;
        justify-content: space-around;
        flex-direction: column;
        align-items: start;
    }

    .mobile-menu-link ul li:first-child {
        transition-delay: 0.2s;
    }

    .social-media {
        grid-row: 1/2;
        grid-column: 3/5;
        justify-items: end;
        align-items: center;
        transition: all 2s linear;
    }

    .social-media .social-media-desktop {
        height: 0;
        display: none;
    }

    .social-media {
        height: 7rem;
        display: flex;
        justify-self: end;
        align-items: center;
    }

    .social-media .hamburger-menu {
        display: block;
        font-size: 2.5rem;
    }
}

@media (max-width: 798px) {
    .main-nav {
        height: 6rem;
        grid-template-columns: 1rem 2fr 1fr 1fr 1rem;
    }

    .logo,
    .social-media ul {
        height: 6rem;
    }

    .social-media {
        height: 6rem;
        display: flex;
        justify-self: end;
        align-items: center;
    }

    .social-media .hamburger-menu {
        display: block;
        font-size: 2.5rem;
    }
}

@media (max-width: 520px) {
    .main-nav {
        height: 6rem;
        grid-template-columns: 1rem 4fr 1fr 1fr 1rem;
    }

    .logo,
    .social-media ul {
        height: 6rem;
    }

    .logo h2 {
        font-size: 2rem;
    }

    .social-media {
        height: 6rem;
        display: flex;
        justify-self: end;
        align-items: center;
    }

    .social-media .hamburger-menu {
        display: block;
        font-size: 2.5rem;
    }

    /* hero section  */

    .hero-section h1 {
        font-size: 3.8rem;
    }
}

.nav-logout {
    text-align: center;
}

.nav-logout p {
    font-weight: 500;
    font-size: 1.4rem;
}

.nav-icons {
    display: flex;
    justify-self: end;
    float: right;
    align-items: center;
}

.nav-logbutton {
    margin: 1rem;
    padding: 6px 5px 5px 20px;
    width: 6rem;
    height: 3rem;
    border-radius: 7%;
    background-color: rgb(22, 12, 65);
    color: aliceblue;
    font-weight: 400;
    font-size: 1.5rem;
}

.nav-logbutton :hover {
    color: aliceblue;
}

.nav-user {
    padding: .8rem 0 0 0;
    margin-left: 2rem;
}
